        pages: data.pages
      });
    }
    return res.json({
      status: 'success',
      data: data
    });
  }

  static error(res, error, status = 400) {
    res.status(status);
    return res.json({
      success: 'failed',
      error: {
        message: error.message,
        code: error.code || 'Invalid',
        errors: error.errors
      }
    });
  }
}

