import dotenv from 'dotenv';

dotenv.config();

export default {
  mongodb: {
    url: process.env.NODE_ENV === 'develop' ? process.env.DATABASE_URL_DEV : process.env.DATABASE_URL,
    secret: '!&!&OJpWXnDtB0eju7OE!zDp20G1JC%6bpq2',
    options: {
      useNewUrlParser: true,
      useCreateIndex: true,
      useFindAndModify: false,
      useUnifiedTopology: true,
      user: process.env.NODE_ENV !== 'develop' && process.env.MONGO_INITDB_ROOT_USERNAME,
      pass: process.env.NODE_ENV !== 'develop' && process.env.MONGO_INITDB_ROOT_PASSWORD
    }
  },

  facebook: {
    clientID: process.env.FACEBOOK_CLIENT_ID,
    clientSecret: process.env.FACEBOOK_CLIENT_SECRET,
    callbackURL: process.env.FACEBOOK_CALLBACK_URL
  },

  google: {
    clientID: process.env.GOOGLE_CLIENT_ID,
    clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    callbackURL: process.env.GOOGLE_CALLBACK_URL
  },

  firebase: {
    private_key: process.env.private_key,
    client_email: process.env.client_email,
    client_x509_cert_url: process.env.client_x509_cert_url
  },

  aws: {
    accessKeyId: process.env.AWS_S3_ACCESS_KEY_ID,
    secretAccessKey: process.env.AWS_S3_SECRET_ACCESS_KEY,
    bucketName: process.env.AWS_STORAGE_BUCKET_NAME
  },

  jwt: {
    secret: process.env.JWT_SECRET
  },

  app: {
    ROLE: {
      ADMIN: 'ADMIN'
    }
  },
  apn: {
    keyId: process.env.APN_KEY_ID,
    teamId: process.env.APN_TEAM_ID,
    topic: process.env.APN_TOPIC,
    production: process.env.APN_PRODUCTION
  },
  iap: {
    IOS: {
      VERIFY_RECEIPT_PRODUCT_URL: 'https://buy.itunes.apple.com/verifyReceipt',
      VERIFY_RECEIPT_SANDBOX_URL:
        'https://sandbox.itunes.apple.com/verifyReceipt'
    }
  },
  mailgun: {
    apiKey: process.env.MAILGUN_API_KEY,
    domain: process.env.MAILGUN_DOMAIN
  }
};

