import express from 'express';
import path from 'path';
import helmet from 'helmet';
import cors from 'cors';
import compression from 'compression';
import morgan from 'morgan';
import bodyParser from 'body-parser';
import passport from 'passport';
import {
  errorHandle,
  notFoundHandle,
  logErrors
} from './helpers/handle-errors';
import { logger, mongoose, swagger } from './services';
import api from './api';
import config from './config';

require('./services/passport');

const app = express();
const rootApi = '/api/v1';
const ROOT_FOLDER = path.join(__dirname, '..');
const SRC_FOLDER = path.join(ROOT_FOLDER, 'src');
// Security
app.use(helmet());
app.use(cors());
// compression
app.use(compression());

// logs http request
app.use(morgan('dev', { stream: logger.stream }));

// parse requests of content-type - application/x-www-form-urlencoded
app.use(bodyParser.urlencoded({ extended: true }));

// parse requests of content-type - application/json
app.use(bodyParser.json());

// passport
app.use(passport.initialize());

// database
mongoose.connect(config.mongodb.url, config.mongodb.options);

app.use(express.static(path.join(ROOT_FOLDER, 'build'), { index: false }));
// app.use('/static', express.static(path.join(SRC_FOLDER, 'public')));
// app.use('/media', express.static(path.join(ROOT_FOLDER, 'uploads')));
// app.get('/', (req, res) =>
//   res.json({ message: 'Welcome to flyin-salon API!' })
// );

app.set('view engine', 'ejs');

app.use(express.static(path.join(ROOT_FOLDER, 'public')));

// app.get('/', (req, res) => {
//   res.render('index')
// })

app.use('/api-docs', swagger());

app.use(rootApi, api);

// The "catchall" handler: for any request that doesn't
// match one above, send back React's index.html file.
app.get('/admin', (req, res) => {
  res.sendFile(path.join(ROOT_FOLDER, 'build', 'index.html'));
});

app.use(notFoundHandle);
app.use(logErrors);
app.use(errorHandle);

export default app;

